INSERT INTO books (isbn, title, author, genre_id, synopsis, cover_img, penerbit)
SELECT
  '9786020449937',
  "BSD Bahagia Sekarang, Menikah Kemudian",
  "Nur`afifah Hasbi Nasution",
  (SELECT id FROM genres WHERE name = "agama"),
  "Status single adalah masalah bagi kebanyakan wanita. Apalagi saat undangan pernikahan silih berganti berdatangan. Ditambah stigma yang beredar luas di masyarakat. Ada anggapan bahwa mereka `gagal` dan `tidak laku`. Parahnya, sebagian orang tak segan mengutarakan pertanyaan-pertanyaan nyelekit pada mereka. Sering kali disertai pula pandangan merendahkan yang tak berperasaan. Akibatnya, sebagian wanita single kehilangan harapan untuk hidup lebih baik. Hingga kata bahagia seakan memudar dari hidup mereka. Terkikis oleh rasa malu dan putus asa yang menjajah jiwa.",
  "bsd-bahagia-sekarang-menikah-kemudian.jpg",
  "Elex Media Komputindo"
WHERE EXISTS (SELECT id FROM genres WHERE name = "agama");

INSERT INTO books (isbn, title, author, genre_id, synopsis, cover_img, penerbit)
SELECT
  '9786230031830',
  "Hijab for Sisters: Jadi Anak Pesantren",
  "Triani Retno A.",
  (SELECT id FROM genres WHERE name = "agama"),
  "Hijab for Sisters: Jadi Anak Pesantren adalah salah satu buku bacaan anak karangan Triani Retno A. Menceritakan kehidupan pergaulan di sekitar anak remaja. Tasya yang anak kota, dengan pergaulan yang bebas dan sering nongkrong bersama teman-temannya mendadak harus tinggal di pesantren. \r\n\r\nPemberontakannya dimulai hingga dia menjadi santri yang sering melakukan pelanggaran. Pertemanannya dengan Dini (Wonogiri), Sarah (bule), Astri (Jakarta) tidak selalu mulus. Apalagi Dini, yang dianggap sebagai musuh. Pergulatan batin dialaminya dari hari ke hari. Pesantren dianggapnya sebagai penjara mewah. Dini yang awalnya dianggap musuh akhirnya dia menjadi orang yang pertama memahami dirinya.\r\n\r\nLika-liku di pesantren pun mengubah pribadi Tasya menjadi lebih baik hingga dia dijadikan pembimbing santri yang lebih muda darinya. Tasya berhasil menjalani kehidupannya di pesantren. Bukankan siapa yang harinya sekarang lebih baik daripada kemarin maka dia termasuk orang yang beruntung. Siapa yang harinya sama dengan kemarin maka dia adalah orang yang merugi.\r\n\r\nKepiawaian penulis, dalam menuturkan tiap cerita dalam bab menunjukkan bahwa beliau adalah penulis hebat. Meskipun usia saat beliau menulis tidak remaja lagi, penggambaran beliau pada situasi psikologis remaja sangat mumpuni. Selain itu penulis juga pandai sekali membuat amanah cerita masuk tanpa kesan menggurui. Bahkan beberapa kutipan hadits dan ayat Al Quran disematkan secara implisit dalam cerita sehingga membaca buku ini menjadi sangat menyenangkan.\r\n\r\nDetail:\r\n\r\nJumlah Halaman\r\n264\r\n\r\nPenerbit\r\nElex Media Komputindo\r\n\r\nTanggal Terbit\r\n3 Apr 2022\r\n\r\nBerat\r\n0.2 kg\r\n\r\nISBN\r\n9786230031830\r\n\r\nLebar\r\n12.5 cm\r\n\r\nBahasa\r\nIndonesia\r\n\r\nPanjang\r\n19.5cm",
  "laiqa-hijab-for-sisters-4-jadi-anak-pesantren.jpg",
  "Elex Media Komputindo"
WHERE EXISTS (SELECT id FROM genres WHERE name = "agama");

