INSERT INTO books (isbn, title, author, genre_id, synopsis, cover_img, penerbit)
SELECT
  '9786232449107',
  "Dasar-Dasar Riset & Analisis Data Pemasaran",
  "Fajar Nuraini Df",
  (SELECT id FROM genres WHERE name = "bisnis-ekonomi"),
  "Buku DASAR-DASAR RISET & ANALISIS DATA PEMASARAN mengulas permasalahan yang berkaitan dengan riset yang biasa dilakukan oleh seorang peneliti dengan berbagai peristiwa dalam kehidupan ekonomi, sosial dan bisnis. Dalam kehidupan ekonomi dan sosial banyak ditemukan berbagai macam permasalahan yang tiada batas ruang dan waktu dengan berbagai macam gejala peristiwa baik yang menguntungkan maupun merugikan. \r\n\r\nBaik yang diharapkan terjadi atau yang tidak diharapkan. Demikian juga pada kehidupan bisnis, sehingga perlu dilakukan riset lebih mendalam baik prediksi masa kini maupun prediksi-prediksi untuk jangka waktu menengah dan panjang. Dimana semua tergantung pada sisi kebutuhan akan suatu nilai yang diharapkan terjadi. \r\n\r\nDalam dunia bisnis salah satu hal terpenting adalah memahami target pasar, karena itu dua hal yang sangat penting untuk dipahami adalah riset pemasaran dan perilaku konsumen. Riset pemasaran adalah sebuah penelitian yang sistematis dan detail untuk menyediakan informasi yang digunakan dalam pengambilan keputusan. \r\n\r\nSedangkan perilaku konsumen adalah kegiatan individu yang secara langsung terlibat dalam mendapatkan, menggunakan, serta mengambil keputusan untuk mengkonsumsi sebuah produk atau lebih. Tujuan buku ini untuk memudahkan pengambilan keputusan untuk perusahaan, dimana sekarang perilaku konsumen selalu berubah-ubah dan sangat susah ditebak. \r\n\r\nBuku ini terdiri dari 9 bab pembahasan, yang mencakup bab 1 tentang riset pemasaran; bab 2 tentang data primer dan sekunder; bab 3 pengambilan sampel riset; bab 4 tentang merancang kuesioner; bab 5 tentang merancang skala kuesioner; bab 6 tentang analisis data : uji statistik parametrik ; bab 7 tentang analisis data : uji statistik nonparametrik; bab 8 seputar teori pemasaran dan pembahasan ditutup bab 9 tentang riset dan teori perilaku konsumen.\r\n\r\nDetail Informasi lain :\r\n\r\n- Pengiriman : minimal 1 hari kerja \r\n- Cover : Soft Cover\r\n- Tebal : 184 Halaman\r\n- Tanggal Terbit: 4 Juni 2023\r\n- ISBN : 9786232449107\r\n- Penulis: Fajar Nuraini Df\r\n- Penerbit : Anak Hebat Indonesia\r\n- Berat : 0.155 kg\r\n- Dimensi : 20 x 14 cm",
  "dasar-dasar-riset-analisis-data-pemasaran.jpg",
  "Anak Hebat Indonesia"
WHERE EXISTS (SELECT id FROM genres WHERE name = "bisnis-ekonomi");

