INSERT INTO books (isbn, title, author, genre_id, synopsis, cover_img, penerbit)
SELECT
  '9786237137511',
  "Tutorial Sederhana Menggunakan OJS 3",
  "I GEDE SUSRAMA DKK",
  (SELECT id FROM genres WHERE name = "komputer-teknologi"),
  "Open Journal Systems (OJS) adalah aplikasi open source yang berfungsi untuk mengelola dan menerbitkan jurnal ilmiah secara online. OJS adalah manajemen jurnal dan sistem penerbitan yang dioperasikan dengan editor yang sangat fleksibel yang dapat diunduh secara gratis dan diinstal pada server Web lokal. Sistem ini telah dirancang untuk mengurangi waktu dan energi yang dicurahkan untuk tugas administrasi dan manajerial yang terkait dengan mengedit jurnal, sekaligus meningkatkan pencatatan dan efisiensi proses editorial. Upaya peningkatan kualitas penerbitan jurnal keilmuan dan publik melalui berbagai inovasi, antara lain meningkatkan pengalaman pembaca, membuat kebijakan jurnal lebih transparan, dan memperbaiki pengindeksan.\r\n\r\nDalam buku Tutorial Sederhana Menggunakan OJS 3 ini akan menjelaskan pedoman dan langkah atau cara mengaplikasikan OJS 3 dalam hal untuk pembuatan Website Jurnal Internasional, mulai dari tutorial instalasi, tutorial pengoprasian OJS 3 sampai menjadi sebuah Web, dan tutorial desain dari OJS 3. \r\n\r\nDaftar Isi\r\nBAB I: Journal Settings\r\nBAB II: Administrations journal\r\nBAB III: Create User\r\nBAB IV: Mengganti User Journal\r\nBAB V: User Profile\r\nBAB VI: Journal Settings\r\nBAB VII: Website Settings\r\nBAB VIII: Workflow Settings\r\nBAB IX: Distributions Settings\r\nBAB X: Workflow\r\nBAB XI: Create Theme\r\nBAB XII: Sitebar Settings\r\nBAB XIII: Static Pages\r\nBAB XIV: Navigation Button\r\n\r\nDetail Buku\r\nJudul: Tutorial Sederhana Menggunakan OJS 3\r\nPenulis: I Gede Susmara, Ariyono Setiawan, Berryl Pamudya Firensha, dan Rizqi Candra Pramana\r\nJumlah Halaman: 96 hlm\r\nTahun Terbit: 30 Desember 2019\r\nPenerbit: Indomedia Pustaka\r\nISBN: 9786237137511\r\nBahasa: Indonesia\r\nBerat Buku: 0,300 kg\r\nDimensi Buku: 17 x 24 cm",
  "tutorial-sederhana-menggunakan-ojs-3.jpg",
  "Indomedia Pustaka"
WHERE EXISTS (SELECT id FROM genres WHERE name = "komputer-teknologi");

INSERT INTO books (isbn, title, author, genre_id, synopsis, cover_img, penerbit)
SELECT
  '9786230102936',
  "Artificial Intelligence",
  "Imam Robandi",
  (SELECT id FROM genres WHERE name = "komputer-teknologi"),
  "Sinopsis:\r\nMateri buku teks ini ditulis oleh penulis dengan gaya umum, walaupun di beberapa bab terasa berat, tetapi sangat menarik untuk Anda baca. Setiap bab mempunyai pendekatan sendiri cara mengungkapkannya, tetapi sebagian besar berbasis pada kecerdasan hidup hewan dalam berkoloni. \r\n\r\nAda 16 bab di sini, yang dimulai di Bab 1 adalah Kecerdasan koloni binatang dan diakhiri di Bab 16 dengan Algoritma Pelacakan dari Ikan Hiu (Shark Search Algorithm, SSA). Di bab 15, Anda disuguhi Algoritma Optimisasi Serigala Abu-abu (Grey Wolf optimization, GWO) dan semua bab di buku ini sangat menarik untuk Anda baca.\r\n\r\nBuku Artificial Intelligence karya Imam Robandi menyampaikan seluk-beluk kecerdasan buatan dengan cara yang unik, yaitu dengan memberikan perumpamaan kecerdasan hidup hewan dalam berkoloni sehingga pembaca dapat memahami isi buku ini lebih baik.\r\n\r\nBuku Artificial Intelligence cocok dibaca oleh setiap kalangan, baik pelajar, mahasiswa, maupun masyarakat umum yang tertarik untuk mengetahui cara kerja kecerdasan buatan atau ingin berkecimpung di bidang ini. Semoga buku ini dapat memberikan gambaran yang utuh tentang kecerdasan buatan.\r\n\r\nSegera dapatkan buku Artificial Intelligence karya Imam Robandi hanya di Gramedia terdekat di kota Anda atau pesan secara online di gramedia.com!\r\n\r\n\r\nInformasi Lainnya:\r\nTerbit: 20 November 2019\r\nPenerbit: Andi Offset\r\nPenulis: Imam Robandi\r\nFormat: soft-cover\r\nISBN: 9786230102936\r\nHarga: Rp 176.000\r\nDimensi: 19 x 23 cm\r\nJumlah halaman: 564",
  "artificial-intelligence.jpg",
  "Penerbit Andi"
WHERE EXISTS (SELECT id FROM genres WHERE name = "komputer-teknologi");

INSERT INTO books (isbn, title, author, genre_id, synopsis, cover_img, penerbit)
SELECT
  '9789792999259',
