INSERT INTO books (isbn, title, author, genre_id, synopsis, cover_img, penerbit)
SELECT
  '207929796',
  "Yamaha - Accoustic Guitar JR-2",
  "Gramedia",
  (SELECT id FROM genres WHERE name = "musik-1"),
  "Detail Produk \r\nMerek: Yamaha\r\nProdusen: Lets Project\r\nUkuran:  84 cm x 32 cm\r\nWarna: Cokelat kayu\r\n\r\nDeskripsi Produk\r\nJR2 adalah gitar akustik yang dibuat mengacu pada FG series. Gitar ini gampang dibawa dan dimainkan di mana saja. \r\nPas dipakai untuk bernyanyi bersama saat di perkemahan, perjalanan atau di mana pun Anda ingin memainkannya. \r\nTerbuat dari mahogany pada sisi depan dan samping, memberikan tampilan cantik serta dilengkapi dengan case yang tahan lama.\r\n3/4 Size FG Body\r\nSolid Spruce Top.\r\nMahogany UTF (Ultra Thin Film) That Offers Beautiful Looks.\r\nOriginal Gig Bag Included.",
