INSERT INTO books (isbn, title, author, genre_id, synopsis, cover_img, penerbit)
SELECT
  '9786230313899',
  "Mega Book - Motor",
  "Picthall & Gunzi",
  (SELECT id FROM genres WHERE name = "nonfiksi-anak-remaja"),
  "Ada banyak jenis motor di jalan.\r\nAda motor balap yang melaju sangat kencang.\r\nAda superbike yang besar dan bertenaga.\r\nAda motor touring yang amat nyaman untuk jarak jauh.\r\nAyo temukan motor-motor menakjubkan lainnya,\r\nseperti motor skuter, motor klasik, dan lainnyabdi dalam buku yang hebat ini!\r\n\r\nSeri Mega Book ditulis oleh para pakar pendidikan\r\ndengan gambar-gambar memukau dan teks yang mudah dibaca,\r\nuntuk membuat membaca menjadi kegiatan yang menyenangkan,\r\ndan menarik bagi anak-anak.\r\n\r\nLengkapi koleksi serinya!\r\n(Kover Mega Book Dinosaurus, Mega Book Hiu, Mega Book Mobil,\r\nMega Book Pesawat, Mega Book Traktor, Mega Book Truk, Mega Book Motor, Mega Book\r\nPesawat Luar Angkasa, Mega Book Tim Penyelamat, Mega Book Hewan, Mega Book Hewan\r\nMungil)\r\n\r\nDeskripsi Buku (untuk GRAMEDIA.COM):\r\nMEGA BOOK MOTOR - Motor-Motor  Terkeren, Tercepat, dan Paling Menakjubkan di Jalan! ditulis oleh para pakar pendidikan, dengan gambar-gambar memukau dan teks yang mudah dibaca untuk membuat membaca menjadi kegiatan yang menyenangkan dan menarik bagi anak-anak.\r\nKeunggulan:\r\n- Ditulis oleh pakar pendidikan\r\n- 32 halaman berwarna berisi informasi tentang motor- motor yang ada di dunia\r\n- Dilengkapi dengan gambar beragam jenis motor yang memukau\r\n- Membuat anak-anak untuk mengenal berbagai jenis sepeda motor\r\n- Memicu rasa ingin tahu anak terhadap motor\r\n- Membuat kegiatan membaca menjadi makin menyenangkan\r\n- Dapat menumbuhkan kecintaan membaca",
  "mega-book-motor.jpg",
  "m&c!"
WHERE EXISTS (SELECT id FROM genres WHERE name = "nonfiksi-anak-remaja");

INSERT INTO books (isbn, title, author, genre_id, synopsis, cover_img, penerbit)
SELECT
  '9786235070964',
  "Beyond Belief: Fact or Fiction",
  "Guru Gembul",
  (SELECT id FROM genres WHERE name = "non-fiksi"),
  "Buku Beyond Belief: Fact or Fiction (Mengungkap yang “Tak Terlihat” Berdasarkan Referensi yang Tepat)\n \nMelalui buku Beyond Belief: Fact or Fiction ini, Pak Guru Gembul mengajak kita berbicara hal-hal yang bernuansa teori konspirasi dan supranatural secara lebih bertanggung jawab, baik dari sisi sumber rujukan, pengambilan kesimpulan, hingga konsekuensinya dalam kehidupan sehari-hari. \nBuku ini terdiri dari empat (4) quarter. Quarter pertama membahas elite global dan organisasi-organisasi rahasia dunia. Quarter kedua membahas fakta fakta tersembunyi di balik sains dan alam semesta. Quarter ketiga membahas tokoh-tokoh yang diselimuti misteri. Sementara itu, pada quarter terakhir, kita diajak untuk merenungi hal-hal yang nyata di sekitar kita.\n\n\nTahun Terbit : Cetakan Pertama, September 2024\n\nPernahkah Anda terpikir betapa menariknya dunia yang terbuka lebar lewat lembaran buku? Membaca bukan hanya kegiatan rutin, tetapi juga petualangan tak terbatas ke dalam imajinasi dan pengetahuan.\n \nMembaca mengasah pikiran, membuka wawasan, dan memperkaya kosakata. Ini adalah pintu menuju dunia di luar kita yang tak terbatas.\n\nTetapkan waktu khusus untuk membaca setiap hari. Dari membaca sebelum tidur hingga menyempatkan waktu di pagi hari, kebiasaan membaca dapat dibentuk dengan konsistensi.\n\nPilih buku sesuai minat dan level literasi. Mulailah dengan buku yang sesuai dengan keinginan dan kemampuan membaca.\n \nTemukan tempat yang tenang dan nyaman untuk membaca. Lampu yang cukup, kursi yang nyaman, dan sedikit musik pelataran bisa menciptakan pengalaman membaca yang lebih baik.\n\nBuat catatan atau jurnal tentang buku yang telah Anda baca. Tuliskan pemikiran, kesan, dan pelajaran yang Anda dapatkan.",
  "beyond-belief-fact-or-fiction.jpg",
  "Yrama Widya"
WHERE EXISTS (SELECT id FROM genres WHERE name = "non-fiksi");

