INSERT INTO books (isbn, title, author, genre_id, synopsis, cover_img, penerbit)
SELECT
  '9786020530130',
  "WANGSIT (Pawang Soal Sulit) HOTS BUMN",
  "TIM PUSAT KARIER SALEMBA",
  (SELECT id FROM genres WHERE name = "buku-pendukung-belajar"),
  "Menjadi pegawai BUMN merupakan dambaan bagi beberapa orang, karena dapat bekerja di bawah naungan perusahaan BUMN akan menjadi jaminan tersendiri untuk kehidupan yang lebih baik terutama dalam bidang ekonomi. Namun untuk masuk BUMN dan mendapatkan keuntungan-keuntungan tersebut bukanlah hal yang mudah karena cukup banyak sekali pesaing yang sama-sama menginginkan posisi tersebut. \r\n\r\nSinopsis Buku: Lolos seleksi BUMN adalah impian hampir sebagian besar masyarakat Indonesia. Berbagai keuntungan yang akan diperoleh jika menjadi pegawai BUMN membuat animo pendaftar BUMN selalu tinggi. Namun, tidak sedikit pula yang gagal dalam tes tersebut. Oleh karena itu, diperlukan persiapan yang matang. Buku WANGSIT (Pawang Soal Sulit) HOTS BUMN ini hadir untuk membekali peserta dalam persiapan seleksi BUMN. Materi dalam buku ini membahas secara mendalam tentang seleksi BUMN, meliputi TKD (Tes Kompetensi Dasar), AKHLAK (Core Values), dan Bahasa Inggris. Selain itu dilengkapi pula dengan informasi lengkap rekrutmen BUMN, tips lolos seleksi BUMN, tips sukses wawancara, dan tips membuat CV yang menarik. Drilling soal per topik bahasan dengan pembahasan yang mudah dipahami dan bank soal simulasi tes juga melengkapi buku ini untuk memberikan gambaran tentang materi yang akan diujikan. Buku ini juga memberikan bonus aplikasi Simulasi Tes Akademik (TPA), TOEFL, Tes Buta Warna Ishihara, try out online, dan Webinar Kupas Tuntas TKD dan AKHLAK bersama Tim Tentor WANGSIT. Selamat belajar dan semoga sukses!",
  "wangsit-pawang-soal-sulit-hots-bumn.jpg",
  "Gramedia Widiasarana Indonesia"
WHERE EXISTS (SELECT id FROM genres WHERE name = "buku-pendukung-belajar");

